import { useEffect, useState } from "react";
import {
  Box,
  Button,
  Center,
  Flex,
  FormControl,
  FormLabel,
  Input,
  Link,
  Text,
} from "@chakra-ui/react";
import Api from "../../../Api";
import { useNavigate } from "react-router-dom";

interface IProps {
  setLogged: React.Dispatch<React.SetStateAction<boolean>>;
}

const Login = ({ setLogged }: IProps): JSX.Element => {
  const [email, setEmail] = useState<string>("");
  const [password, setPassword] = useState<string>("");
  const navigate = useNavigate();

  useEffect(() => {
    const token: object = JSON.parse(localStorage.getItem("t"));
    setLogged(token ? true : false);
  }, []);

  const submit = async () => {
    try {
      const datos = { email, password };
      const token = await Api.post("/auth/login", datos);
      localStorage.setItem("t", JSON.stringify(token.data));
      setLogged(true);
      navigate("/");
      alert("Usu√°rio Logado");
    } catch (error) {
      console.log(error);
      alert(error);
    }
  };

  return (
    <Box
      width={"100vw"}
      height={"100vh"}
      display={"flex"}
      justifyContent={"center"}
      alignItems={"center"}
      backgroundImage={
        "data:image/png;base64,"
      }
      backgroundRepeat={"repeat"}
    >
      <Box
        width={"100vw"}
        height={"100vh"}
        position={"absolute"}
        backgroundColor={"#80808080"}
      ></Box>
      <Flex
        margin={"auto"}
        flexDirection={"column"}
        alignItems={"center"}
        gap={"1rem"}
        borderRadius={"1rem"}
        width={"20%"}
        justifyContent={"center"}
        height={"37%"}
        padding={"2rem"}
        boxShadow={"1rem 1rem 3rem 0"}
        backgroundColor={"white"}
        zIndex={"2"}
      >
        <Text fontSize="2rem" fontWeight="bold" mb={4}>
          Login
        </Text>
        <FormControl>
          <FormLabel>Email</FormLabel>
          <Input
            type="email"
            placeholder="Seu email"
            padding={"0.6rem"}
            width={"20rem"}
            borderRadius={"0.6rem"}   
            border = {"1px solid"}
            borderColor= {"#380b61"}      
            onChange={(e) => {
              setEmail(e.target.value);        
            }}
            _hover={{          
              border: "1px solid",
              transform: "scale(1.01)",  
              borderColor: "#380b61",    
            }}
          />
        </FormControl>

        <FormControl mt={4}>
          <FormLabel>Senha</FormLabel>
          <Input
            type="password"
            placeholder="Sua senha"
            padding={"0.6rem"}
            width={"20rem"}
            borderRadius={"0.6rem"}
            border = {"1px solid"}
            borderColor= {"#380b61"}
            onChange={(e) => {
              setPassword(e.target.value);
            }}
            _hover={{          
              border: "1px solid",
              transform: "scale(1.01)",  
              borderColor: "#380b61",    
            }}
          />
        </FormControl>

        <Flex width={"21rem"} fontSize={"medium"}>
          <Text mt={2}>
            <Link color="#59429d" href="#recover">
              Esqueceu sua senha?
            </Link>
          </Text>
        </Flex>

        <Button
          onClick={submit}
          mt={4}
          colorScheme="teal"
          width="full"
          padding={"0.7rem 4rem"}
          borderRadius={"0.6rem"}
          cursor={"pointer"}
          backgroundColor={"#59429e"}
          border={"none"}
          color={"white"}
          transition={"300ms linear"}
          _hover={{
            color: "#white",
            border: "1px solid",
            background: "#380b61",
            transform: "scale(1.02)",      
          }}
        >
          Entrar
        </Button>

        <Text mt={2}>
          N√£o possu√≠ uma conta?{" "}
          <Link color="#59429d" href="#register">
            Criar Conta
          </Link>
        </Text>
      </Flex>
    </Box>
  );
};

export default Login;
